		let obj: kwebapp.clientData;
		let e: HTMLElement|null;
		obj = <kwebapp.clientData>JSON.parse(v);
		e = document.getElementById('client');
		new kwebapp.client(obj).fill(e);
	}
};
xmh.open('GET', '/cgi-bin/app?client-id=123', true);
